class Ball:
    def __init__(self, x, y, radius, color) -> None:
        self.x = x
        self.y = y
        self.radius = radius
        self.color = color
        self.yVel = 4 + multiplier
        self.hit = False
        if len(balls) <= 5:
            balls.append(self)
        pygame.draw.circle(screen, self.color,
                           (self.x, self.y), self.radius, 0)

    def move(self) -> None:
        self.y += self.yVel
        pygame.draw.circle(screen, (0, 0, 0), (self.x, self.y), self.radius, 0)

        if self.y + self.radius > screen.get_height():
            balls.remove(self)


class Stick:
    def __init__(self, img, x, lives) -> None:
        self.img = img
        self.x = x
        self.y = pygame.display.Info().current_h - img.get_height()
        self.lives = lives
        screen.blit(self.img, (self.x, self.y))

    def move(self) -> None:
        mouse_x, _ = pygame.mouse.get_pos()
        self.x = mouse_x
        if mouse_x < 0:
            self.x = 0
        if mouse_x + self.img.get_width() > screen.get_width():
            self.x = screen.get_width() - self.img.get_width()
        screen.fill((255, 255, 255))
        screen.blit(self.img, (self.x, self.y))

    def collide(self, other: Ball) -> bool:
        if self.x < other.x < self.x + self.img.get_width():
            if self.y < other.y < self.y + self.img.get_height():
                other.color = (255, 0, 0)
                return True
        return False


