def pause():
    global paused

    if paused:
        paused = False
    else:
        font = pygame.font.SysFont("comicsansms", 50)
        text = font.render("Paused", True, (0, 0, 0))
        screen.blit(text, (screen.get_width()/2 - text.get_width() /
                    2,  screen.get_height()/2 - text.get_height()/2))
        paused = True


def main():
    global end_time, multiplier, balls, Ball, Stick, paused, clock, FPS, heart, stickImg, pause
    stick = Stick(stickImg, 0, 3)
    run = True
    while run:
        clock.tick(FPS)

        for event in pygame.event.get():
            if event.type == pygame.QUIT:
                run = False

            if event.type == pygame.KEYDOWN:
                if event.key == pygame.K_ESCAPE:
                    run = False
                if event.key == pygame.K_q:
                    run = False
                if event.key == pygame.K_p:
                    pause()

                # if event.key == pygame.K_j:
                #     stick.collide = lambda _: False
                # if event.key == pygame.K_k:

                #     def collide(self, other: Ball) -> bool:
                #         if self.x < other.x < self.x + self.img.get_width():
                #             if self.y < other.y < self.y + self.img.get_height():
                #                 other.color = (255, 0, 0)
                #                 return True
                #         return False

                #     stick.collide = collide

        if not paused:
            Ball(random.randint(0, screen.get_width()),
                 random.randint(-1000, 0), 20, (0, 0, 0))

            stick.move()
            for i in balls:
                i.move()
                if stick.collide(i):
                    if stick.lives > 1:
                        try:
                            balls.remove(i)
                        except:
                            pass
                        stick.lives -= 1
                    elif stick.lives == 1:
                        balls.remove(i)
                        stick.lives -= 1
                        run = False

            font = pygame.font.SysFont("comicsansms", 50)

            global score

            score = str(int((end_time)*multiplier))
            text = font.render(
                "Score: " + score, True, (0, 0, 0))
            screen.blit(text, (10, 30))
            multiplier += 0.0005

            end_time += (1/multiplier)/200

            # Life
            hearts = [heart] * stick.lives
            font = pygame.font.SysFont("comicsansms", 50)
            text = font.render("Lives:", True, (0, 0, 0))
            screen.blit(text, (575, 30))
            x = 680
            for i in hearts:
                screen.blit(i, (x, 38))
                x += 35

            font = pygame.font.SysFont("comicsansms", 30)

            text = font.render("Press P To Pause/Resume", True, (0, 0, 0))
            screen.blit(text, (545, 10))
            text = font.render("Press Q/ESC To Quit", True, (0, 0, 0))
            screen.blit(text, (10, 10))

        pygame.display.update()
    quitter()


def quitter():

    global end_time, multiplier

    screen.fill((255, 255, 255))

    font = pygame.font.SysFont("comicsansms", 50)
    text1 = font.render("Game Over", True, (0, 0, 0))
    screen.blit(text1, (screen.get_width()/2 - text1.get_width() /
                        2,  screen.get_height()/2 - text1.get_height()/2 - 60))
    # text = font.render(f"Your Score Was {score}", True, (0, 0, 0))
    # screen.blit(text, (screen.get_width()/2 - text.get_width() /
    #                    2,  screen.get_height()/2 - text.get_height()/2 - 30))

    font = pygame.font.SysFont("comicsansms", 35)
    text = font.render("Press Y To Continue", True, (0, 0, 0))
    screen.blit(text, (screen.get_width()/2 - text.get_width() /
                       2,  screen.get_height()/2 - text1.get_height()/2 + 40))
    text = font.render("Press N To Exit", True, (0, 0, 0))
    screen.blit(text, (screen.get_width()/2 - text.get_width() /
                       2,  screen.get_height()/2 - text1.get_height()/2 + 70))

    pygame.display.update()

    while True:
        for event in pygame.event.get():
            if event.type == pygame.QUIT:
                if not stickPresent:
                    os.remove("stick.png")
                if not heartPresent:
                    os.remove("heart.png")
                pygame.quit()
                quit()
            if event.type == pygame.KEYDOWN:
                if event.key == pygame.K_y:
                    end_time = 1
                    multiplier = 1
                    balls.clear()
                    main()
                if event.key == pygame.K_n:
                    if not stickPresent:
                        os.remove("stick.png")
                    if not heartPresent:
                        os.remove("heart.png")
                    pygame.quit()
                    quit()


main()
